from django.urls import pathfrom . import viewsurlpatterns = [    path('', views.HomeApiViews.as_view(), name='home'),    path('<int:pk>/', views.DetailApiViews.as_view(), name='detail-page'),    path('<int:pk>/update/', views.UpdateApiViews.as_view(), name='update'),    path('new/', views.CreateApiViews.as_view(), name='create-page'),    path('<int:pk>/delete/', views.DeleteApiViews.as_view(), name='delete'),    path('<str:username>/', views.UserPostsApiViews.as_view(), name='users'),    path('sort/<str:field>/', views.SortByField.as_view(), name='sorts'),]